import "./TesterPage.css"
import Bug from "./components/bug-component/Bug";
import BugForm from "./components/bug-component/add-bug-component/BugForm";
import {useEffect, useState} from "react";

const TesterPage = () => {
    const token = "Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJQb3BWYXMxMjMiLCJpYXQiOjE2ODM5MDQ2MDQsImV4cCI6MTY4MzkwODIwNH0.EiQkP9qayMsk1FR4vjMBaNF1IdpU6BFljkN7ES9mAqE"
    const [bugs, setBugs] = useState(null)

    const getAllBugs = async () => {
        const response = await fetch("http://localhost:8080/api/v1/bugs",
            {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": token
                }
            })
        const data = await response.json()
        console.log(data)
        return data
    }

    useEffect(() => {
        async function fetchData() {
            const bugsFromResponse = await getAllBugs()
            setBugs(bugsFromResponse)
        }

        fetchData()
    }, [])
    return <div id={"tester-container"}>
        <div id={"bugs-container"}>
            {bugs && bugs.map(bug =>
                <Bug id={bug.id} bugName={bug.name} description={bug.description}
                     priority={bug.priority}
                     status={bug.status} postDate={bug.postDate}>

                </Bug>
            )}
        </div>
        <BugForm></BugForm>

    </div>
}

export default TesterPage
