import "./programmerPage.css"
import {useEffect, useState} from "react";
import Bug from "../tester-page/components/bug-component/Bug";
import BugForm from "../tester-page/components/bug-component/add-bug-component/BugForm";

const ProgrammerPage = () => {
    const token = "Bearer " + sessionStorage.getItem("authToken")
    const [unresolvedBugs, setUnresolvedBugs] = useState(null)

    const getAllUnresolvedBugs = async () => {
        const response = await fetch("http://localhost:8080/api/v1/bugs?status=UNSOLVED",
            {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": token
                }
            })
        if (response.status === 200) {
            const data = await response.json()
            console.log(data)
            return data
        }
        return null
    }

    useEffect(() => {
        async function fetchData() {
            const bugsFromResponse = await getAllUnresolvedBugs()
            if (bugsFromResponse !== null) {
                setUnresolvedBugs(bugsFromResponse)
            }
        }
        fetchData()

        const interval = setInterval(async () => {
            await fetchData()
        }, 3000)
        return () => clearInterval(interval)

    }, [])

    return <div id={"programmer-container"}>
        <div id={"bugs-container"}>
            {unresolvedBugs && unresolvedBugs.map(bug =>
                <Bug id={bug.id} bugName={bug.name} description={bug.description}
                     priority={bug.priority}
                     status={bug.status} postDate={bug.postDate}>

                </Bug>
            )}
        </div>
        <BugForm></BugForm>

    </div>
}

export default ProgrammerPage
